export let bgColor="";export let borderColor="";let setColor=(o,r)=>{o.forEach(o=>{o.style.color=r})};export const getPriceColor=(o,r,e)=>{o<0?(setColor([r,e],"green"),bgColor=borderColor="green"):0==o?(setColor([r,e],"gray"),bgColor=borderColor="gray"):(setColor([r,e],"red"),bgColor=borderColor="red")};