let spinner=document.querySelector(".spinner"),filters=document.querySelector(".filters");export const removeChilds=e=>{for(;e.lastChild;)e.removeChild(e.lastChild)};export const debounce=(e,r)=>{let t;return(...l)=>{t&&clearTimeout(t),t=setTimeout(()=>{e(...l)},r)}};export const disableFilters=()=>{if("Ocurri\xf3 un error"===document?.querySelector(".modal-window").firstChild.outerText){spinner.style.display="none";for(let e=0;e<=5;e++)filters[e].disabled=!0}};